--
-- Structure de la table `brands`
--
CREATE TABLE IF NOT EXISTS `brands`
(
    `id`         int(11)             NOT NULL AUTO_INCREMENT,
    `brand`      varchar(50)         NOT NULL,
    PRIMARY KEY (`id`),
    UNIQUE KEY `brand_uq` (`brand`)
) ENGINE=InnoDB DEFAULT CHARSET=utf8;



--
-- Structure de la table `section`
--
CREATE TABLE IF NOT EXISTS `section` (
 `id` int(11) NOT NULL AUTO_INCREMENT,
 `type` varchar(25) NOT NULL DEFAULT '',
 `title` varchar(100) NOT NULL DEFAULT '',
 `titlenl` varchar(100) NOT NULL DEFAULT '',
 `parent` varchar(25) DEFAULT NULL,
 `sortby` int(4) DEFAULT NULL,
 PRIMARY KEY (`id`),
 UNIQUE KEY type_uq (`type`),
 FOREIGN KEY (`parent`) REFERENCES `section`(`type`)
) ENGINE=InnoDB DEFAULT CHARSET=utf8 AUTO_INCREMENT=81;




--
-- Structure de la table `product`
--
CREATE TABLE IF NOT EXISTS `product` (
  `id` int(11) NOT NULL AUTO_INCREMENT,
  `type` varchar(25) NOT NULL DEFAULT '',
  `brand` varchar(50) NOT NULL DEFAULT '',
  `name` varchar(50) NOT NULL DEFAULT '',
  `description` varchar(1800) DEFAULT NULL,
  `picture` varchar(300) DEFAULT NULL,
  `handle` varchar(50) DEFAULT NULL,
  `steel` varchar(50) DEFAULT NULL,
  `size` varchar(50) DEFAULT NULL,
  `youtube_ref` varchar(30) DEFAULT NULL,
  `price` float DEFAULT NULL,
  `old_price` float DEFAULT NULL,
  `comment` varchar(100) DEFAULT NULL,
  `promo` boolean NOT NULL default false,
  `instock` boolean NOT NULL default true,
  PRIMARY KEY (`id`),
  KEY `name` (`name`),
  FOREIGN KEY (`type`) REFERENCES `section`(`type`),
  FOREIGN KEY (`brand`) REFERENCES `brands`(`brand`)
) ENGINE=InnoDB  DEFAULT CHARSET=utf8 AUTO_INCREMENT=738;

