--
-- Structure de la table `slider`
--
CREATE TABLE IF NOT EXISTS `slider` (
  `id` int(11) NOT NULL AUTO_INCREMENT,
  `image` varchar(50) NOT NULL,
  `title1` varchar(50) DEFAULT NULL,
  `title2` varchar(255) DEFAULT NULL,
  `description` text,
  `link` varchar(50),
  PRIMARY KEY (`id`)
) ENGINE=InnoDB AUTO_INCREMENT=4 DEFAULT CHARSET=utf8;



--
-- Structure de la table `top_sales`
--
CREATE TABLE IF NOT EXISTS `top_sales` (
  `product_id` int(11) NOT NULL,
  `sales` int(11) NOT NULL,
  PRIMARY KEY (`product_id`)
) ENGINE=InnoDB DEFAULT CHARSET=utf8;


--
-- Structure de la table `country`
--
CREATE TABLE IF NOT EXISTS `country` (
  `id` int(11) NOT NULL,
  `code` int(11) NOT NULL,
  `alpha2` varchar(2) NOT NULL,
  `alpha3` varchar(3) NOT NULL,
  `name_en` varchar(50) NOT NULL,
  `name_fr` varchar(50) NOT NULL,
   PRIMARY KEY (`id`),
   UNIQUE KEY `country_code_uindex` (`code`),
   UNIQUE KEY `country_alpha3_uindex` (`alpha3`),
   UNIQUE KEY `country_alpha2_uindex` (`alpha2`)
) ENGINE=InnoDB DEFAULT CHARSET=utf8;



--
-- Structure de la table `user_order`
--
CREATE TABLE IF NOT EXISTS `user_order` (
  `id` int(11) NOT NULL AUTO_INCREMENT,
  `user_id` varchar(50) NOT NULL,
  `order_date` timestamp NOT NULL DEFAULT CURRENT_TIMESTAMP,
  `order_json` longtext NOT NULL,
  PRIMARY KEY (`id`)
) ENGINE=InnoDB DEFAULT CHARSET=utf8;


--
-- Structure de la table `video`
--
CREATE TABLE IF NOT EXISTS `video` (
